32 #ifndef _MEMORY_MANAGER_H
33 #define _MEMORY_MANAGER_H
34 
35 #include "semaphore.h"
36 
48 class MemoryManager {
49   public:
50     // Constructor for MemoryManager class
51     // Throws MemoryManagerError::kBadSizing If the maximum size is less than 1
52     MemoryManager(int, bool = false);
53 
54     // Destructor for MemoryManager.
55     // Frees the buffers inside both queues and then frees the queues
56     ~MemoryManager();
57 
58     // Pushes a memory buffer into the input queue.
59     bool PushIntoIn(void *);
60 
61     // Pushes a memory buffer into the output queue.
62     bool PushIntoOut(void *);
63 
64     // Pops a memory buffer from the input queue.
65     // Throws MemoryManagerError::kNullPtr If the content to return is
66     // null (it can't be processed)
67     void *PopFromIn();
68 
69     // Pops a memory buffer from the output queue.
70     // Throws MemoryManagerError::kNullPtr If the content to return is null (it
71     // can't be processed)
72     void *PopFromOut();
73 
74     // Loads a memory buffer into the queues.
75     void LoadMemoryManager(void *);
76 
77     // Returns the number of memory buffers in the input queue.
78     int in_queue_count();
79 
80     // Returns the number of memory buffers in the output queue.
81     int out_queue_count();
82 
83     // Returns the maximum size of the memory buffer queues.
84     int max_size();
85 
86     // Tries to get the ownership of the cpu resources to do any action
87     void wait_finish();
88 
96     enum MemoryManagerError {
97         kBadSizing, 
98         kNullPtr,   
99     };
100 
101   private:
102     void **in_queue_;  
103     void **out_queue_; 
104     int max_size_;     
106     int rear_in_iterator_;   
107     int front_in_iterator_;  
108     int rear_out_iterator_;  
109     int front_out_iterator_; 
111     std::atomic<int>
112         in_queue_count_; 
113     std::atomic<int>
114         out_queue_count_; 
116     Semaphore *in_semaphore_;  
117     Semaphore *out_semaphore_; 
119     std::mutex push_in_mtx_;  
120     std::mutex push_out_mtx_; 
121     std::mutex pop_in_mtx_;   
122     std::mutex pop_out_mtx_;  
124     bool debug_; 
125 };
126 
127 #endif
